import React, { useState, useEffect } from 'react';

const createBoard = (dim) => {
    let boardDim = dim * 2 + 1;
    let board = [];
    for (i = 0; i < board_dim; i++) {
        let row = [];
        for (j = 0; j < board_dim; j++) {
            if (i == 0 || j == 0 || i == board_dim - 1 || j == board_dim - 1
                || i % 2 == 0 || j % 2 == 0) row.push(1); // walls on edges or even squares
            else row.push(0);
        }
        board.push(row);
    } 
    return board;
};

const generate = (board) => {
    for (i = 0; i < size; i++) {
        for (j = 0; j < size; j++) {
            //
        }
    }
}

function MazeGen() {
    const size = 20; // number of rows/columns

    return (
        <div>
            <div className="mazeBox">
                <h1>Maze Page</h1>
                <p>Enjoy the maze game!</p>
                <button className="startMaze" onClick={generate}>Generate Maze</button>
                <div className="maze">
                    {board.map((row, rowIndex) => (
                        <div key={rowIndex} style={{ display: 'flex' }}>
                            {row.map((cell, colIndex) => (
                                <div key={colIndex} style={{
                                    width: '20px',
                                    height: '20px',
                                    backgroundColor: cell === 1 ? 'black' : 'white',
                                    border: cell === 1 ? '1px solid black' : '1px solid gray'
                                }}></div>
                            ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

const MazeApp = () => {
    <div>
        <div className="mazeBox">
            <h1>Maze Page</h1>
            <p>Enjoy the maze game!</p>
            <button className="startMaze" onClick={generate}>Generate Maze</button>
            <Maze rows={21} cols={21}/>
        </div>
    </div>
}

export default Maze;
