            let row = [];
            for (j = 0; j < board_dim; j++) {
                if (i == 0 || j == 0 || i == board_dim - 1 || j == board_dim - 1
                    || i % 2 == 0 || j % 2 == 0) row.push(1); // walls on edges or even squares
                else row.push(0);
            }
            board.push(row);
        } 
        return board;
    };

