import React, { useState, useEffect } from 'react';

const generate = (dim) => {
    // generate board
    let boardDim = dim * 2 + 1;
    let board = [];
    let walls = [];
    for (let i = 0; i < boardDim; i++) {
        let row = [];
        for (let j = 0; j < boardDim; j++) {
            if (i == 0 || j == 0 || i == boardDim - 1 || j == boardDim - 1) {
                    row.push(1);
                } // walls on edges
            else if (i % 2 == 0 || j % 2 == 0) {
                row.push(1);
                walls.push([i,j]);
            } // walls on even squares
            else {
                row.push([0,[[i,j]]]);
            };
        }
        board.push(row);
    } 

    while (walls.length > 0) {
        let i = Math.floor(Math.random * walls.length);
        let adj = [];
        if (i-1>0 && board[i-1,j][0] == 0) adj.push(board[i-1,j]); // add adj
        if (j-1>0 && board[i,j-1][0] == 0) adj.push(board[i,j-1]);
        if (i+1<boardDim && board[i+1,j][0] == 0) adj.push(board[i+1,j]);
        if (j+1<boardDim && board[i,j+1][0] == 0) adj.push(board[i,j+1]);

        //let adjI = 0;   // remove spaces with duplicate sets
        for (let adjI = 0; adjI < adj.length; adjI++) 
        {
            for (let adjJ = 0; j < adjJ.length; adjJ++) {
                if (adjI != adjJ && adj[adjI][1] == adj[adjJ][1]) {
                    adj.splice()
                    break;
                }
            }
        }

    }
    return board;
};

// STORE THE SET IN THE SQUARE

