import React, { useState, useEffect } from 'react';

const generate = (dim) => {
    // generate board
    let boardDim = dim * 2 + 1;
    let board = [];
    let walls = [];
    for (let i = 0; i < boardDim; i++) {
        let row = [];
        for (let j = 0; j < boardDim; j++) {
            if (i == 0 || j == 0 || i == boardDim - 1 || j == boardDim - 1
                || i % 2 == 0 || j % 2 == 0) {
                    row.push(1);
                    walls.push([i,j]);
                } // walls on edges or even squares
            else {
                row.push([0,[[i,j]]]);
            };
        }
        board.push(row);
    } 

    while (walls.length > 0) {

    }
    return board;
};

// STORE THE SET IN THE SQUARE

/*
const generate = (board) => {
    for (i = 0; i < size; i++) {
        for (j = 0; j < size; j++) {
            //
        }
    }
}
*/

function Maze({dim}) {
    const [maze, setMaze] = useState([]);
    const size = 20; // number of rows/columns

    useEffect(() => {
        const initialMaze = generate(dim);
        setMaze(initialMaze);
    }, [dim]);

    return (
        <div>
            {maze.map((row, rowIndex) => (
                <div key={rowIndex} style={{ display: 'flex' }}>
                    {row.map((cell, colIndex) => (
                        <div key={colIndex} style={{
                            width: '1.5vh',
                            height: '1.5vh',
                            backgroundColor: cell === 1 ? 'black' : 'white',
                            //border: cell === 1 ? '1px solid black' : '1px solid gray'
                        }}></div>
                    ))}
                </div>
            ))}
        </div>
    );
}

const MazeApp = () => {
    return (
        <div>
            <div className="mazeBox">
                <h1>Maze Page</h1>
                <p>Enjoy the maze game!</p>
                <button className="startMaze" onClick={generate}>Generate Maze</button>
                <Maze dim={20}/>
            </div>
        </div>
    );
}

export default MazeApp;
